UseTemplate.REENERGIZE_IGNITION_CIRCUIT_CODE:
(A:TURB ENG IGNITION SWITCH EX1:1, enum) 2 != if{
	2 (>K:TURBINE_IGNITION_SWITCH_SET1)
}

(A:TURB ENG IGNITION SWITCH EX1:2, enum) 2 != if{
	2 (>K:TURBINE_IGNITION_SWITCH_SET2)
}

UseTemplate.ON_AP_MASTER_SWITCH:
(A:AUTOPILOT MASTER, Bool) if{
	(A:AUTOPILOT FLIGHT DIRECTOR ACTIVE, Bool) ! if{ 
		1 (>K:TOGGLE_FLIGHT_DIRECTOR)
	}
}

UseTemplate.UPDATE_CODE:
(A:GEAR HANDLE POSITION, Bool) (A:GEAR POSITION, percent) 99.9 > and (>O:_ShouldBeConnectedToPowerGrid)
(O:_ShouldBeConnectedToPowerGrid) sp0
2 (>A:BUS LOOKUP INDEX, Number)
(A:CIRCUIT CONNECTION ON:14, Bool) l0 != if{ 14 2 (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE) }
(A:CIRCUIT CONNECTION ON:15, Bool) l0 != if{ 15 2 (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE) }
1 (>A:BUS LOOKUP INDEX, Number)
(A:CIRCUIT CONNECTION ON:16, Bool) l0 != if{ 16 1 (>K:2:ELECTRICAL_BUS_TO_CIRCUIT_CONNECTION_TOGGLE) }
